import React from "react";

export const ContactPicker = (props) => {

  const contactsArray = props.contacts;
  const contactOnChange = props.onChange;

  return (
    <select onChange={contactOnChange} style={{display: "block", width : "30vw", margin: "10px"}}>
      <option value="No contact is selected" selected="selected">No contact is Selected</option>
      {contactsArray.map( contact => <option value={contact.name}>{contact.name}</option>)}
    </select>
  );
};
