import React from "react";

export const ContactPicker = ({
    contacts,
    onChange
  }) => {

  const contactsArray = contacts;
  const contactOnChange = onChange;

  return (
    <select onChange={contactOnChange} style={{display: "block", width : "30vw", margin: "10px"}}>
      <option value="No contact is selected" selected="selected">No contact is Selected</option>
      {
        Object.values(contactsArray).map( (value, index) => {
          return <option value={value} key={index}>{value}</option>;
        })
      }
    </select>
  );
};
